contracts/FeeDistributor.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

contract FeeDistributor is Ownable, ReentrancyGuard {
    IERC20 public immutable imaliToken;
    address public treasury;
    address public team;
    address public buybackPool;

    uint256 public treasuryShare = 50;
    uint256 public teamShare = 30;
    uint256 public buybackShare = 20;
    bool public distributionPaused;

    event Distribution(address indexed caller, uint256 treasuryAmount, uint256 teamAmount, uint256 buybackAmount);
    event SharesUpdated(uint256 treasury, uint256 team, uint256 buyback);

    constructor(
        address _imaliToken,
        address _treasury,
        address _team,
        address _buybackPool
    ) {
        require(_imaliToken != address(0), "Invalid token");
        require(_treasury != address(0), "Invalid treasury");
        require(_team != address(0), "Invalid team");
        require(_buybackPool != address(0), "Invalid buyback pool");

        imaliToken = IERC20(_imaliToken);
        treasury = _treasury;
        team = _team;
        buybackPool = _buybackPool;
    }

    function distribute() external nonReentrant {
        require(!distributionPaused, "Distribution paused");
        uint256 balance = imaliToken.balanceOf(address(this));
        require(balance > 0, "No balance");

        uint256 treasuryAmount = (balance * treasuryShare) / 100;
        uint256 teamAmount = (balance * teamShare) / 100;
        uint256 buybackAmount = balance - treasuryAmount - teamAmount;

        require(imaliToken.transfer(treasury, treasuryAmount), "Treasury transfer failed");
        require(imaliToken.transfer(team, teamAmount), "Team transfer failed");
        require(imaliToken.transfer(buybackPool, buybackAmount), "Buyback transfer failed");

        emit Distribution(msg.sender, treasuryAmount, teamAmount, buybackAmount);
    }

    function setShares(uint256 _treasury, uint256 _team, uint256 _buyback) external onlyOwner {
        require(_treasury + _team + _buyback == 100, "Shares must sum to 100");
        treasuryShare = _treasury;
        teamShare = _team;
        buybackShare = _buyback;
        emit SharesUpdated(_treasury, _team, _buyback);
    }

    function setRecipients(address _treasury, address _team, address _buyback) external onlyOwner {
        require(_treasury != address(0) && _team != address(0) && _buyback != address(0), "Invalid address");
        treasury = _treasury;
        team = _team;
        buybackPool = _buyback;
    }

    function setPaused(bool paused) external onlyOwner {
        distributionPaused = paused;
    }

    function recoverERC20(address tokenAddress, uint256 amount) external onlyOwner {
        require(tokenAddress != address(imaliToken), "Cannot recover IMALI");
        IERC20(tokenAddress).transfer(owner(), amount);
    }
}

contracts/IMALIStaking.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract IMALIStaking is Ownable, ReentrancyGuard {
    IERC20 public immutable imaliToken;
    IERC20 public immutable lpToken;
    IERC721 public immutable nftContract;

    struct StakeInfo {
        uint256 amount;
        uint256 lastUpdated;
        uint256 rewards;
        uint256 rewardMultiplier;
    }

    mapping(address => StakeInfo) public imaliStakers;
    mapping(address => StakeInfo) public lpStakers;
    mapping(uint256 => uint256) public nftTierMultipliers; // NFT ID â†’ multiplier (100 = 1x)
    mapping(address => uint256) public userBoostMultiplier;

    uint256 public imaliRewardRate = 50;
    uint256 public lpRewardRate = 100;
    uint256 public constant MAX_FEE = 10; // 10%

    address public feeRecipient;
    bool public emergencyPaused;

    event Staked(address indexed user, bool isIMALI, uint256 amount, uint256 fee);
    event Unstaked(address indexed user, bool isIMALI, uint256 amount, uint256 fee);
    event RewardClaimed(address indexed user, uint256 reward);
    event EmergencyPaused(bool paused);

    modifier whenNotPaused() {
        require(!emergencyPaused, "Contract is paused");
        _;
    }

    constructor(
        address _imaliToken,
        address _lpToken,
        address _nftContract,
        address _feeRecipient
    ) {
        require(_imaliToken != address(0), "Invalid IMALI token");
        require(_lpToken != address(0), "Invalid LP token");
        require(_nftContract != address(0), "Invalid NFT contract");
        require(_feeRecipient != address(0), "Invalid fee recipient");

        imaliToken = IERC20(_imaliToken);
        lpToken = IERC20(_lpToken);
        nftContract = IERC721(_nftContract);
        feeRecipient = _feeRecipient;
    }

    function stakeIMALI(uint256 amount, uint256[] memory nftIds) external nonReentrant whenNotPaused {
        _stake(amount, nftIds, true);
    }

    function stakeLP(uint256 amount, uint256[] memory nftIds) external nonReentrant whenNotPaused {
        _stake(amount, nftIds, false);
    }

    function _stake(uint256 amount, uint256[] memory nftIds, bool isIMALI) internal {
        require(amount > 0, "Amount must be > 0");

        IERC20 token = isIMALI ? imaliToken : lpToken;
        uint256 feePercentage = isIMALI ? 3 : 5;
        uint256 feeAmount = (amount * feePercentage) / 100;
        uint256 stakeAmount = amount - feeAmount;

        // Transfer tokens
        require(token.transferFrom(msg.sender, feeRecipient, feeAmount), "Fee transfer failed");
        require(token.transferFrom(msg.sender, address(this), stakeAmount), "Stake transfer failed");

        // Update rewards before modifying stake
        _updateRewards(msg.sender, isIMALI);

        // Apply NFT boosts if provided
        if (nftIds.length > 0) {
            _applyNFTBoosts(msg.sender, nftIds);
        }

        // Update stake
        StakeInfo storage stake = isIMALI ? imaliStakers[msg.sender] : lpStakers[msg.sender];
        stake.amount += stakeAmount;
        stake.lastUpdated = block.timestamp;

        emit Staked(msg.sender, isIMALI, stakeAmount, feeAmount);
    }

    function _applyNFTBoosts(address user, uint256[] memory nftIds) internal {
        uint256 totalMultiplier = 100; // Base 100 = 1x

        for (uint i = 0; i < nftIds.length; i++) {
            require(nftContract.ownerOf(nftIds[i]) == user, "Not NFT owner");
            totalMultiplier += nftTierMultipliers[nftIds[i]];
        }

        userBoostMultiplier[user] = totalMultiplier;
    }

    function unstakeIMALI(uint256 amount) external nonReentrant whenNotPaused {
        _unstake(amount, true);
    }

    function unstakeLP(uint256 amount) external nonReentrant whenNotPaused {
        _unstake(amount, false);
    }

    function _unstake(uint256 amount, bool isIMALI) internal {
        StakeInfo storage stake = isIMALI ? imaliStakers[msg.sender] : lpStakers[msg.sender];
        require(amount > 0 && stake.amount >= amount, "Invalid amount");

        _updateRewards(msg.sender, isIMALI);

        IERC20 token = isIMALI ? imaliToken : lpToken;
        uint256 feePercentage = isIMALI ? 3 : 5;
        uint256 feeAmount = (amount * feePercentage) / 100;
        uint256 unstakeAmount = amount - feeAmount;

        stake.amount -= amount;

        require(token.transfer(feeRecipient, feeAmount), "Fee transfer failed");
        require(token.transfer(msg.sender, unstakeAmount), "Unstake transfer failed");

        emit Unstaked(msg.sender, isIMALI, unstakeAmount, feeAmount);
    }

    function claimRewards() external nonReentrant whenNotPaused {
        _updateRewards(msg.sender, true);
        _updateRewards(msg.sender, false);

        uint256 totalReward = imaliStakers[msg.sender].rewards + lpStakers[msg.sender].rewards;
        require(totalReward > 0, "No rewards");

        imaliStakers[msg.sender].rewards = 0;
        lpStakers[msg.sender].rewards = 0;

        require(imaliToken.transfer(msg.sender, totalReward), "Reward transfer failed");
        emit RewardClaimed(msg.sender, totalReward);
    }

    function _updateRewards(address user, bool isIMALI) internal {
        StakeInfo storage stake = isIMALI ? imaliStakers[user] : lpStakers[user];
        uint256 rate = isIMALI ? imaliRewardRate : lpRewardRate;

        if (stake.amount > 0) {
            uint256 timeStaked = block.timestamp - stake.lastUpdated;
            uint256 baseReward = (stake.amount * rate * timeStaked) / 1 days;
            uint256 boostedReward = (baseReward * userBoostMultiplier[user]) / 100;
            stake.rewards += boostedReward;
        }

        stake.lastUpdated = block.timestamp;
    }

    // Admin functions
    function setNFTMultipliers(uint256[] memory nftIds, uint256[] memory multipliers) external onlyOwner {
        require(nftIds.length == multipliers.length, "Array length mismatch");
        for (uint i = 0; i < nftIds.length; i++) {
            nftTierMultipliers[nftIds[i]] = multipliers[i];
        }
    }

    function setFeeRecipient(address _feeRecipient) external onlyOwner {
        require(_feeRecipient != address(0), "Invalid address");
        feeRecipient = _feeRecipient;
    }

    function setEmergencyPause(bool paused) external onlyOwner {
        emergencyPaused = paused;
        emit EmergencyPaused(paused);
    }

    function recoverERC20(address tokenAddress, uint256 amount) external onlyOwner {
        require(tokenAddress != address(imaliToken) && tokenAddress != address(lpToken), "Cannot recover staked tokens");
        IERC20(tokenAddress).transfer(owner(), amount);
    }
}

contracts/LPLottery.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@chainlink/contracts/src/v0.8/VRFConsumerBase.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract LPLottery is VRFConsumerBase, Ownable {
    IERC20 public immutable lpToken;
    uint256 public lotteryDuration = 7 days;
    uint256 public lastLotteryTime;
    uint256 public prizePool;

    mapping(address => uint256) public tickets;
    address[] public participants;
    bool public lotteryActive;

    // Chainlink VRF
    bytes32 internal keyHash;
    uint256 internal fee;
    uint256 public randomResult;

    event LotteryEntered(address participant, uint256 amount);
    event WinnerSelected(address winner, uint256 prize);
    event EmergencyWithdraw(address recipient, uint256 amount);

    constructor(
        address _lpToken,
        address _vrfCoordinator,
        address _linkToken,
        bytes32 _keyHash,
        uint256 _fee
    ) VRFConsumerBase(_vrfCoordinator, _linkToken) {
        require(_lpToken != address(0), "Invalid LP token");
        lpToken = IERC20(_lpToken);
        keyHash = _keyHash;
        fee = _fee;
        lotteryActive = true;
    }

    function enterLottery(uint256 lpAmount) external {
        require(lotteryActive, "Lottery not active");
        require(lpAmount > 0, "Amount must be > 0");
        require(
            lpToken.transferFrom(msg.sender, address(this), lpAmount),
            "Transfer failed"
        );

        if (tickets[msg.sender] == 0) {
            participants.push(msg.sender);
        }

        tickets[msg.sender] += lpAmount;
        prizePool += lpAmount;

        emit LotteryEntered(msg.sender, lpAmount);
    }

    function drawWinner() external onlyOwner {
        require(block.timestamp > lastLotteryTime + lotteryDuration, "Too early");
        require(prizePool > 0, "No prize pool");
        require(LINK.balanceOf(address(this)) >= fee, "Not enough LINK");

        lastLotteryTime = block.timestamp;
        requestRandomness(keyHash, fee);
    }

    function fulfillRandomness(bytes32, uint256 randomness) internal override {
        uint256 totalTickets;
        for (uint i = 0; i < participants.length; i++) {
            totalTickets += tickets[participants[i]];
        }

        uint256 winningTicket = randomness % totalTickets;
        uint256 cumulativeTickets;
        address winner;

        for (uint i = 0; i < participants.length; i++) {
            cumulativeTickets += tickets[participants[i]];
            if (cumulativeTickets >= winningTicket) {
                winner = participants[i];
                break;
            }
        }

        uint256 prize = prizePool;
        prizePool = 0;

        // Reset for next round
        for (uint i = 0; i < participants.length; i++) {
            delete tickets[participants[i]];
        }
        delete participants;

        require(lpToken.transfer(winner, prize), "Prize transfer failed");
        emit WinnerSelected(winner, prize);
    }

    function setLotteryActive(bool active) external onlyOwner {
        lotteryActive = active;
    }

    function emergencyWithdraw() external onlyOwner {
        require(!lotteryActive, "Lottery still active");
        uint256 balance = lpToken.balanceOf(address(this));
        require(lpToken.transfer(owner(), balance), "Withdraw failed");
        emit EmergencyWithdraw(owner(), balance);
    }

    function withdrawLink() external onlyOwner {
        require(LINK.transfer(owner(), LINK.balanceOf(address(this))), "LINK transfer failed");
    }
}

